module.exports = function (grunt) {
	grunt.config.init({
		jshint: {
			all: {
				src: ['lib/**/*.js', 'Gruntfile.js'],
				options: {
					nonew: true,
					devel: true,
					eqnull: true
				}
			}
		},
		watch: {
			options: {
				nospawn: true
			},
			all: {
				files: 'lib/**/*',
				tasks: ['test']
			}
		}
	});

	grunt.loadNpmTasks('grunt-contrib-jshint');
	grunt.loadNpmTasks('grunt-contrib-watch');

	grunt.registerTask('dev', ['test', 'watch']);
	grunt.registerTask('test', 'jshint');
};
